import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules/socket";
import { Box, Typography, Paper } from "@mui/material";

function Room({ username, mute }) {
  const [gameState, setGameState] = useState(null);
  const [user, setUser] = useState(username);
  const { roomID } = useParams();
  const [err, setErr] = useState(false);

  // Handle player leaving
  const handleLeave = useCallback((newState) => {
    if (!newState || !Array.isArray(newState.players) || newState.players.length === 0) {
      setErr(true);
    } else {
      setGameState({ ...newState });
    }
  }, []);

  // Fetch game state on load
  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL = process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);
        if (res.status === 200 && res.data && res.data.players) {
          setGameState(res.data);
        } else {
          setErr(true);
        }
      } catch (error) {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);
    getGameState();

    return () => {
      socket.off("leave", handleLeave);
    };
  }, [roomID, handleLeave]);

  // Cleanup on unload
  useEffect(() => {
    const cleanup = () => {
      socket.emit("leave", { user, roomID });
    };
    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [user, roomID]);

  // Render main content based on state
  const renderContent = () => {
    if (err) return <Typography color="error" variant="h6">Room does not exist</Typography>;
    if (!gameState) return <Typography variant="h6">Loading...</Typography>;

    if (user === "" || !gameState.gameStart) {
      return (
        <Wait
          user={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUser}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={user}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <Typography variant="h6">Loading...</Typography>;
  };

  // Chat panel
  const showChat = () => {
    if (
      !err &&
      gameState &&
      Array.isArray(gameState.players) &&
      gameState.players.includes(user) &&
      gameState.players.length > 1
    ) {
      return <Chat user={user} roomID={roomID} />;
    }
    return null;
  };

  return (
    <Box className="room-container" sx={{ display: "flex", flexDirection: "column", gap: 2, p: 2 }}>
      {/* Room Code Display */}
      <Paper
        elevation={3}
        sx={{
          p: 2,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          backgroundColor: "#f5f5f5",
          borderRadius: 2,
        }}
      >
        <Typography variant="h6" fontWeight="bold">
          ðŸŽ® Room Code: {roomID.toUpperCase()}
        </Typography>
      </Paper>

      {/* Main Game / Wait / NewWord Content */}
      <Box sx={{ flex: 1, display: "flex", flexDirection: "column" }}>{renderContent()}</Box>

      {/* Chat Panel */}
      {showChat() && (
        <Box sx={{ mt: 2 }}>
          {showChat()}
        </Box>
      )}
    </Box>
  );
}

export default Room;
