import React, { useState, useEffect, useRef, useCallback } from "react";
import { FormControl, Input, InputLabel, Typography } from "@mui/material";
import Letters from "./Letters";
import Timer from "./Timer";
import "../css/Game.scss";
import { socket } from "../modules/socket";

const figureMapping = [
  "none.png",
  "head.png",
  "body.png",
  "left_leg.png",
  "right_leg.png",
  "left_arm.png",
  "right_arm.png",
  "hair.png",
  "eyes.png",
  "nose.png",
  "mouth.png",
];

function Game({ gameState, setGameState, username, roomID, mute }) {
  const [word, setWord] = useState("");
  const [source, setSource] = useState("");
  const audioRef = useRef(null);

  /** ---------------- GAME STATE UPDATE ---------------- */
  const gameHandler = useCallback(
    (newState) => setGameState({ ...newState }),
    [setGameState]
  );

  /** ---------------- SOUND FX ---------------- */
  const updateSong = (src) => {
    setSource(src);
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.load();
      audioRef.current.play().catch(() => {});
    }
  };

  const handleStatus = useCallback(
    (info) => {
      let newURL = "";
      if (info.status === "timer") newURL = `${process.env.REACT_APP_SERVER}/audio/timer.mp3`;
      else if (info.status === "correct") newURL = `${process.env.REACT_APP_SERVER}/audio/correct.mp3`;
      else if (info.status === "incorrect") newURL = `${process.env.REACT_APP_SERVER}/audio/wrong.mp3`;
      else if (info.status === "win") newURL = "";

      updateSong(newURL);
    },
    []
  );

  /** ---------------- SOCKET LISTENERS ---------------- */
  useEffect(() => {
    socket.on("update", gameHandler);
    socket.on("status", handleStatus);

    return () => {
      socket.off("update", gameHandler);
      socket.off("status", handleStatus);
    };
  }, [gameHandler, handleStatus]);

  /** ---------------- INPUT VALIDATION ---------------- */
  const validateGuess = () => {
    const field = document.getElementById("guess");
    if (!field) return;

    if (gameState.guessedWords.includes(field.value)) field.setCustomValidity(`${field.value} already guessed`);
    else if (!/^[^\s]+(\s+[^\s]+)*$/.test(field.value)) field.setCustomValidity("No leading/trailing spaces");
    else if (!/^[-\sa-zA-Z]+$/.test(field.value)) field.setCustomValidity("Only letters allowed");
    else if (field.value.length < 2) field.setCustomValidity("At least 2 characters required");
    else field.setCustomValidity("");
  };

  /** ---------------- GUESS HANDLERS ---------------- */
  const makeGuess = (entity) => socket.emit("guess", { roomID, gameState: { ...gameState, curGuess: entity } });
  const onFormSubmit = (e) => { e.preventDefault(); makeGuess(word); setWord(""); };
  const onLetterClick = (e) => { e.preventDefault(); setWord(""); makeGuess(e.currentTarget.value); };

  /** ---------------- PREV GUESSER SOUND ---------------- */
  let prevGuesser = gameState.players.indexOf(gameState.guesser);
  do {
    prevGuesser =
      (((prevGuesser - 1) % gameState.players.length) +
        gameState.players.length) %
      gameState.players.length;
  } while (gameState.players[prevGuesser] === gameState.hanger);

  return (
    <div className="game-container" style={{ padding: "1rem", gap: "0.5rem" }}>
      {/* Audio FX */}
      {username === gameState.players[prevGuesser] && source && (
        <audio autoPlay onEnded={() => setSource("")} muted={mute} ref={audioRef}>
          <source src={source} />
        </audio>
      )}

      {/* Top Bar: Timer + Guesses Remaining */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "0.5rem" }}>
        {gameState.time && username === gameState.guesser && (
          <Timer gameState={gameState} makeGuess={makeGuess} />
        )}
        <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
          Guesses Remaining: {gameState.lives - gameState.numIncorrect}
        </Typography>
      </div>

      {/* Category + Hanger Word */}
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "0.5rem" }}>
        <Typography variant="subtitle1">
          Category: <strong>{gameState.category}</strong>
        </Typography>
        {username === gameState.hanger && (
          <Typography variant="subtitle1">
            Word: <strong>{gameState.word}</strong>
          </Typography>
        )}
      </div>

      {/* Guessed Word */}
      <Typography variant="h4" style={{ textAlign: "center", marginBottom: "0.5rem" }}>
        {gameState.guessedWord}
      </Typography>

      {/* Hangman Image */}
      <div style={{ textAlign: "center", marginBottom: "0.5rem" }}>
        <img
          className="drawing"
          src={`/images/${figureMapping[gameState.numIncorrect]}`}
          alt="Hangman"
          style={{ maxWidth: "200px", width: "100%" }}
        />
      </div>

      {/* Guess Section */}
      {username !== gameState.hanger && (
        <div className="inputs-section" style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
          <form onSubmit={onFormSubmit} style={{ marginBottom: "0.25rem" }}>
            <FormControl fullWidth>
              <InputLabel htmlFor="guess">Enter Word Guess</InputLabel>
              <Input
                type="text"
                value={word}
                onChange={(e) => setWord(e.target.value)}
                id="guess"
                name="guess"
                inputProps={{ maxLength: 50, minLength: 2 }}
                onInput={validateGuess}
                disabled={gameState.guesser !== username}
                required
              />
            </FormControl>
          </form>

          <Letters
            onClick={onLetterClick}
            disabled={gameState.guesser !== username}
            guessedLetters={gameState.guessedLetters}
          />
        </div>
      )}
    </div>
  );
}

export default Game;
