import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules/socket";
import { Box, Card, Typography, List, ListItem, ListItemText } from "@mui/material";

function Room({ username, mute }) {
  const [gameState, setGameState] = useState();
  const [user, setUser] = useState(username);
  const { roomID } = useParams();
  const [err, setErr] = useState(false);

  const handleLeave = useCallback((newState) => {
    if (newState && newState.players?.length === 0) setErr(true);
    setGameState({ ...newState });
  }, []);

  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL = process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);
        if (res.status === 200) setGameState(res.data);
      } catch (error) {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);
    getGameState();

    return () => socket.off("leave", handleLeave);
  }, [roomID, handleLeave]);

  useEffect(() => {
    const cleanup = () => {
      socket.emit("leave", { user, roomID });
    };
    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [user, roomID]);

  const renderContent = () => {
    if (err) return <p>Room does not exist</p>;
    if (!gameState) return <p>Loading...</p>;

    if (user === "" || !gameState.gameStart) {
      return (
        <Wait
          user={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUser}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={user}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <p>Loading...</p>;
  };

  const showChat = () => {
    if (
      !err &&
      gameState &&
      Array.isArray(gameState.players) &&
      gameState.players.includes(user) &&
      gameState.players.length > 1
    ) {
      return <Chat user={user} roomID={roomID} />;
    }
    return null;
  };

  return (
    <Box
      sx={{
        display: "flex",
        height: "100vh",
        width: "100%",
        background: "linear-gradient(135deg, #fdfbfb 0%, #ebedee 100%)",
      }}
    >
      {/* Left Panel */}
      <Card
        sx={{
          width: { xs: "30%", md: "300px" },
          minWidth: 250,
          backgroundColor: "#fff",
          boxShadow: 3,
          borderRadius: 2,
          p: 2,
          display: "flex",
          flexDirection: "column",
        }}
      >
        <Typography variant="h6" sx={{ fontWeight: "bold", mb: 2 }}>
          Room Code
        </Typography>
        <Box
          sx={{
            mb: 3,
            p: 1.5,
            borderRadius: 1,
            backgroundColor: "#1976d2",
            color: "#fff",
            textAlign: "center",
            fontWeight: "bold",
            letterSpacing: 2,
          }}
        >
          {roomID || "Loading..."}
        </Box>

        <Typography variant="h6" sx={{ fontWeight: "bold", mb: 1 }}>
          Players
        </Typography>
        <List>
          {gameState?.players?.map((p) => (
            <ListItem key={p} sx={{ p: 0.5 }}>
              <ListItemText primary={p} />
            </ListItem>
          ))}
        </List>
      </Card>

      {/* Right Panel */}
      <Box
        sx={{
          flex: 1,
          p: 2,
          display: "flex",
          flexDirection: "column",
          overflowY: "auto",
        }}
      >
        {renderContent()}
        {showChat()}
      </Box>
    </Box>
  );
}

export default Room;
