import React from 'react';

const HangmanVisual = ({ strikes, maxStrikes }) => {
  const percentage = (strikes / maxStrikes) * 100;
  
  return (
    <div className="mb-6">
      <div className="relative">
        {/* Progress bar */}
        <div className="w-full bg-gray-200 rounded-full h-6 mb-3 overflow-hidden">
          <div
            className={`h-6 rounded-full transition-all duration-500 ${
              percentage > 75 ? 'bg-red-600' :
              percentage > 50 ? 'bg-orange-500' :
              percentage > 25 ? 'bg-yellow-500' :
              'bg-green-500'
            }`}
            style={{ width: `${percentage}%` }}
          >
            <div className="h-full flex items-center justify-center text-white text-xs font-bold">
              {percentage > 15 && `${strikes}/${maxStrikes}`}
            </div>
          </div>
        </div>

        {/* Strike indicators */}
        <div className="flex justify-between mb-2">
          {Array.from({ length: maxStrikes }, (_, i) => (
            <div
              key={i}
              className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-bold transition-all ${
                i < strikes
                  ? 'bg-red-500 text-white scale-110'
                  : 'bg-gray-200 text-gray-400'
              }`}
            >
              {i < strikes ? '✕' : '○'}
            </div>
          ))}
        </div>
      </div>

      <p className="text-center text-sm font-medium text-gray-600 mt-2">
        {strikes === 0 && 'No strikes yet - good luck!'}
        {strikes > 0 && strikes < maxStrikes && `${maxStrikes - strikes} strikes remaining`}
        {strikes >= maxStrikes && 'Out of strikes!'}
      </p>
    </div>
  );
};

export default HangmanVisual;
