import React, { useState, useEffect, useRef } from 'react';

function Chat({ socket, roomId, playerId, messages }) {
  const [messageInput, setMessageInput] = useState('');
  const [localMessages, setLocalMessages] = useState(messages || []);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    setLocalMessages(messages || []);
  }, [messages]);

  useEffect(() => {
    const handleNewMessage = (message) => {
      setLocalMessages(prev => [...prev, message]);
    };

    socket.on('new-message', handleNewMessage);

    return () => {
      socket.off('new-message', handleNewMessage);
    };
  }, [socket]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [localMessages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    
    if (!messageInput.trim()) return;

    socket.emit('send-message', {
      roomId,
      playerId,
      message: messageInput.trim()
    });

    setMessageInput('');
  };

  return (
    <div className="bg-gray-50 rounded-xl p-6 flex flex-col h-full">
      <h3 className="text-xl font-bold text-gray-800 mb-4">Chat</h3>
      
      <div className="flex-1 overflow-y-auto space-y-3 mb-4 max-h-96 scrollbar-hide">
        {localMessages.length === 0 ? (
          <div className="text-center text-gray-400 italic text-sm py-8">
            No messages yet
          </div>
        ) : (
          localMessages.map(msg => (
            <div 
              key={msg.id} 
              className={`rounded-lg p-3 ${
                msg.playerId === playerId 
                  ? 'bg-indigo-100 border-l-4 border-indigo-500' 
                  : 'bg-white border-l-4 border-gray-300'
              }`}
            >
              <div className="flex justify-between items-center mb-1">
                <span className="font-bold text-sm text-gray-800">
                  {msg.playerName}
                </span>
                <span className="text-xs text-gray-500">
                  {new Date(msg.timestamp).toLocaleTimeString([], { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  })}
                </span>
              </div>
              <div className="text-gray-700 text-sm break-words">
                {msg.message}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSendMessage} className="flex gap-2">
        <input
          type="text"
          value={messageInput}
          onChange={(e) => setMessageInput(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 px-4 py-2 border-2 border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          maxLength={200}
        />
        <button 
          type="submit"
          className="px-6 py-2 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold rounded-lg transition-colors"
        >
          Send
        </button>
      </form>
    </div>
  );
}

export default Chat;
