import { useState, useEffect } from "react";
import WordDisplay from "./WordDisplay";
import GuessInput from "./GuessInput";
import PlayerList from "./PlayerList";
import Chat from "./Chat";

export default function GameRoom({ socket, playerId, roomState: initialRoomState }) {
  const [roomState, setRoomState] = useState(initialRoomState);
  const [word, setWord] = useState("");

  useEffect(() => {
    // listen for game updates from server
    socket.on("game-state", (state) => setRoomState(state));
    socket.on("round-started", (state) => setRoomState(state));
    socket.on("waiting-for-word", (state) => setRoomState(state));
    socket.on("round-ended", (state) => setRoomState(state));

    return () => {
      socket.off("game-state");
      socket.off("round-started");
      socket.off("waiting-for-word");
      socket.off("round-ended");
    };
  }, [socket]);

  const isWordSetter = roomState.wordSetterId === playerId;
  const roundInProgress = roomState.roundInProgress;

  const submitWord = () => {
    if (!word || word.trim().length < 3) {
      return alert("Word must be at least 3 letters");
    }
    socket.emit("set-word", { roomId: roomState.roomId, playerId, word });
    setWord(""); // reset input
  };

  return (
    <div>
      <h2 className="text-lg font-bold">Round {roomState.currentRound + 1}</h2>
      <PlayerList players={roomState.players} />

      {!roundInProgress && isWordSetter ? (
        <div>
          <p>You are the Word Setter! Set the secret word:</p>
          <input
            type="text"
            value={word}
            onChange={(e) => setWord(e.target.value)}
            placeholder="Enter secret word"
          />
          <button onClick={submitWord}>Set Word</button>
        </div>
      ) : (
        <>
          <WordDisplay revealed={roomState.secretWordRevealed || ""} />
          <p>Global Strikes: {roomState.globalStrikes}/{roomState.maxStrikes}</p>

          {/* Show GuessInput only to players who are not the word setter */}
          {!isWordSetter && roundInProgress && (
            <GuessInput socket={socket} roomId={roomState.roomId} playerId={playerId} />
          )}
        </>
      )}

      <Chat socket={socket} roomId={roomState.roomId} playerId={playerId} />
    </div>
  );
}
