import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

function GameRoom({ roomId, playerName }) {
  const [players, setPlayers] = useState([]);
  const [currentWord, setCurrentWord] = useState("");
  const [inputLetter, setInputLetter] = useState("");
  const [status, setStatus] = useState("");

  useEffect(() => {
    socket.emit("joinRoom", { roomId, playerName });

    socket.on("playerList", (data) => setPlayers(data));

    socket.on("gameStarted", (data) => {
      setCurrentWord(data.blanks);
      setStatus("Game Started!");
    });

    socket.on("updateWord", (data) => setCurrentWord(data.word));

    socket.on("gameOver", ({ message, answer }) => {
      setStatus(message + (answer ? ` Answer: ${answer}` : ""));
    });
  }, []);

  const sendGuess = () => {
    if (!inputLetter) return;

    socket.emit("guessLetter", { roomId, letter: inputLetter });
    setInputLetter("");
  };

  return (
    <div className="p-10 bg-gray-900 text-white h-screen">
      <h1 className="text-3xl font-bold">Room: {roomId}</h1>

      <div className="mt-5">
        <h2 className="text-xl">Players:</h2>
        <ul className="list-disc ml-5">
          {players.map((p) => (
            <li key={p.id}>{p.name}</li>
          ))}
        </ul>
      </div>

      <div className="mt-10 text-4xl tracking-widest">{currentWord}</div>

      <div className="mt-10">
        <input
          className="px-4 py-2 text-black rounded"
          value={inputLetter}
          onChange={(e) => setInputLetter(e.target.value)}
        />
        <button
          onClick={sendGuess}
          className="ml-4 bg-blue-600 px-6 py-3 rounded-xl"
        >
          Guess
        </button>
      </div>

      <div className="mt-10 text-xl text-yellow-400">{status}</div>
    </div>
  );
}

export default GameRoom;
