import React, { useState, useRef, useEffect } from 'react';
import socketService from '../services/socket';
import { useGame } from '../context/GameContext';

const ChatBox = () => {
  const { messages, roomId, playerId } = useGame();
  const [chatMessage, setChatMessage] = useState('');
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    if (chatMessage.trim() && socketService.getSocket()) {
      socketService.emit('send-message', {
        roomId,
        playerId,
        message: chatMessage.trim()
      });
      setChatMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-lg p-6 flex flex-col h-full">
      <h3 className="text-xl font-bold mb-4 text-gray-800">ðŸ’¬ Chat</h3>
      
      <div className="flex-1 overflow-y-auto space-y-2 mb-4 min-h-0">
        {messages.length === 0 ? (
          <p className="text-center text-gray-400 text-sm mt-4">No messages yet...</p>
        ) : (
          messages.map((msg) => (
            <div key={msg.id} className="p-2 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors">
              <span className="font-semibold text-sm text-blue-600">
                {msg.playerName}:
              </span>
              <span className="text-sm text-gray-700 ml-2">{msg.message}</span>
              <p className="text-xs text-gray-400 mt-1">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </p>
            </div>
          ))
        )}
        <div ref={chatEndRef} />
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          value={chatMessage}
          onChange={(e) => setChatMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type a message..."
          className="flex-1 px-3 py-2 border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none transition-colors"
        />
        <button
          onClick={sendMessage}
          disabled={!chatMessage.trim()}
          className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white px-4 py-2 rounded-lg transition-colors font-medium"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
