import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules/socket";
import { Box, Typography, Paper, List, ListItem, ListItemText, Button, TextField } from "@mui/material";

function Room({ username: initialUsername, mute }) {
  const { roomID } = useParams();
  const [gameState, setGameState] = useState(null);
  const [username, setUsername] = useState(() => localStorage.getItem("username") || initialUsername || "");
  const [err, setErr] = useState(false);
  const [tempName, setTempName] = useState("");

  const handleLeave = useCallback((newState) => {
    if (!newState || !Array.isArray(newState.players) || newState.players.length === 0) {
      setErr(true);
    } else {
      setGameState({ ...newState });
    }
  }, []);

  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL = process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);
        if (res.status === 200 && res.data && res.data.players) {
          setGameState(res.data);
        } else {
          setErr(true);
        }
      } catch (error) {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);
    getGameState();

    return () => socket.off("leave", handleLeave);
  }, [roomID, handleLeave]);

  useEffect(() => {
    const cleanup = () => {
      if (username) socket.emit("leave", { user: username, roomID });
    };
    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [username, roomID]);

  const handleSetUsername = () => {
    if (tempName.trim() === "") return;
    setUsername(tempName.trim());
    localStorage.setItem("username", tempName.trim());
    setTempName("");
  };

  const renderContent = () => {
    if (err) return <Typography color="error">Room does not exist</Typography>;
    if (!gameState) return <Typography>Loading...</Typography>;

    if (!username) {
      // Show input form to enter username
      return (
        <Box sx={{ display: "flex", flexDirection: "column", alignItems: "center", gap: 2, mt: 4 }}>
          <Typography variant="h6">Enter your name to join the room</Typography>
          <TextField
            label="Username"
            value={tempName}
            onChange={(e) => setTempName(e.target.value)}
          />
          <Button variant="contained" onClick={handleSetUsername}>
            Join
          </Button>
        </Box>
      );
    }

    if (!gameState.gameStart || username === "") {
      return (
        <Wait
          user={username}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUsername}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={username}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={username}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <Typography>Loading...</Typography>;
  };

  return (
    <Box
      className="room-container"
      sx={{ display: "flex", flexDirection: "column", height: "100vh", p: 2, gap: 2 }}
    >
      <Box sx={{ display: "flex", flex: 1, gap: 12 }}>
        {/* Left Column (Room code + Players + Chat) */}
        <Paper
          elevation={3}
          sx={{
            flex: 1,
            display: "flex",
            flexDirection: "column",
            gap: 2,
            p: 2,
            borderRadius: 2,
            overflowY: "auto",
            maxHeight: "100%",
          }}
        >
          {/* Room Code */}
          <Typography variant="h6" fontWeight="bold" textAlign="center" sx={{ bgcolor: "lightblue", p: 1, borderRadius: 1 }}>
            ðŸŽ® Room Code: {roomID.toUpperCase()}
          </Typography>

          {/* Players List */}
          <Box sx={{ bgcolor: "#eee", p: 1, borderRadius: 1 }}>
            <Typography variant="subtitle1" fontWeight="bold">
              Players
            </Typography>
            <List dense>
              {gameState?.players?.map((p, idx) => (
                <ListItem key={p}>
                  <ListItemText primary={`${idx + 1}. ${p}`} />
                </ListItem>
              ))}
            </List>
          </Box>

          {/* Chat (only if user has entered username) */}
          {username && <Chat user={username} roomID={roomID} />}
        </Paper>

        {/* Right Column (Game / Wait / NewWord) */}
        <Box sx={{ flex: 2, overflowY: "auto" }}>{renderContent()}</Box>
      </Box>
    </Box>
  );
}

export default Room;
